mapper.select:
select * from ( select t.user_id, t.loan_id, u.login_type, u.login_key, u.name, u.mobile, u.id_num, u.province, u.city, u.address, u.card_bank, u.card_no, u.card_tel, u.company, u.income, u.company_address, u.work_province, u.work_city, u.work_tel, u.degree, u.school, u.seasame_score, u.mobile_ser_code, u.is_done, t.loan_full_id, t.loan_money, t.loan_day, t.start_day, t.expire_day, t.pay_fee, t.act_money, t.Interest, t.overdue_fee, t.bank_type, t.bank_card, t.modifytime, ifnull(apply_state,t.state) as apply_state, a.first_assign_user, a.review_assign_user, a.version from (select * from p2p.loan_list where loan_id not in( select loan_id from p2p.loan_apply_t where apply_state!=2 and apply_state!=3 or first_assign_user!=#{last_updated_by})) t left join p2p.loan_apply_t a on t.loan_id=a.loan_id left join p2p.user_info u on t.user_id=u.user_id) s
AND loan_id = #{loan_id}
AND apply_state = #{state,jdbcType=INTEGER}
AND loan_money = #{loan_money}
AND modifytime = #{modifytime}
AND name = #{name,jdbcType=VARCHAR}
AND loan_money = #{id_num}
AND first_assign_user = #{first_assign_user}
AND two_assign_user = #{two_assign_user}
order by modifytime desc

select * from ( select t.user_id, t.loan_id, u.login_type, u.login_key, u.name, u.mobile, u.id_num, u.province, u.city, u.address, u.card_bank, u.card_no, u.card_tel, u.company, u.income, u.company_address, u.work_province, u.work_city, u.work_tel, u.degree, u.school, u.seasame_score, u.mobile_ser_code, u.is_done, t.loan_full_id, t.loan_money, t.loan_day, t.start_day, t.expire_day, t.pay_fee, t.act_money, t.Interest, t.overdue_fee, t.bank_type, t.bank_card, t.modifytime, ifnull(apply_state,t.state) as apply_state, a.first_assign_user, a.review_assign_user, a.version from (select * from p2p.loan_list where loan_id not in( select loan_id from p2p.loan_apply_t where apply_state not in(3,4,5) or review_assign_user!=#{last_updated_by})) t left join p2p.loan_apply_t a on t.loan_id=a.loan_id left join p2p.user_info u on t.user_id=u.user_id) s
AND loan_id = #{loan_id}
AND apply_state = #{apply_state,jdbcType=INTEGER}
AND loan_money = #{loan_money}
AND modifytime = #{modifytime}
AND name = #{name,jdbcType=VARCHAR}
AND loan_money = #{id_num}
AND first_assign_user = #{first_assign_user}
AND two_assign_user = #{two_assign_user}
order by modifytime desc
