mapper.select:
select rule_id,rule_name,state,created_by,created_date,last_updated_by,last_updated_date,remark from p2p.rule_t

	
		AND rule_name = #{rule_name}
	
	
		AND state = #{state}
	
	
		AND created_by = #{created_by}

select dis_id,rule_id,table_name,column_name,semanteme,value,arithmetic,created_by,created_date,last_updated_by,last_updated_date,remark from p2p.dimension_t

	
		AND rule_id = #{rule_id}
	
	
		AND table_name = #{table_name}
	
	
		AND column_name = #{column_name}
	
	
		AND created_by = #{created_by}
